package textout;import java.io.PrintStream;import java.io.PrintWriter;import org.xml.sax.Locator;import org.xml.sax.SAXException;public class TextException extends SAXException {    private String message = null;    public TextException(String message, Exception exception, Locator loc) {        super (message, exception);        if (message == null) message = "No message";        if (loc != null) {            message = message + " (line " + loc.getLineNumber() +                " column " + loc.getColumnNumber() + ")";        }        if (exception != null) {            message = message + ": " + exception.getMessage();        }        this.message = message;    }    @Override    public String getMessage() {        return(message);    }    @Override    public String toString() {        StringBuffer buffer = new StringBuffer();        buffer.append(this.getClass().getName());        buffer.append(": ");        buffer.append(getMessage());        return(buffer.toString());    }    @Override    public void printStackTrace(PrintStream out) {        this.printStackTrace(new PrintWriter(out));    }    @Override    public void printStackTrace(PrintWriter out) {        super.printStackTrace(out);        if (getException() != null) {            out.print("Nested exception: ");            getException().printStackTrace(out);        }        out.flush();    }    @Override    public void printStackTrace() {        this.printStackTrace(System.err);    }}